            function spamcheck($rawr)
  {
  //filter_var() sanitizes the e-mail
  //address using FILTER_SANITIZE_EMAIL
  $rawr=filter_var($rawr, FILTER_SANITIZE_EMAIL);

  //filter_var() validates the e-mail
  //address using FILTER_VALIDATE_EMAIL
  if(filter_var($rawr, FILTER_VALIDATE_EMAIL))
    {
    return TRUE;
    }
  else
    {
    return FALSE;
    }
  }

if (isset($_REQUEST['email']) && isset($_REQUEST['subject']) && isset($_REQUEST['message']))
  {//if everything is entered, go on

  //check if the email address is invalid
  $mailcheck = spamcheck($_REQUEST['email']);
  if ($mailcheck==FALSE)
    {
    echo "That email does not exist.";
    }
  else
    {//send email
    $email = $_REQUEST['email'] ;
    $subject = $_REQUEST['subject'] ;
    $message = $_REQUEST['message'] ;
    mail("support@infinity-forum.com", "Subject: $subject",
    $message, "From: $email" );
    echo "Your mail has been sent.";
    }
  }
else
  {//if "email" is not filled out, display the form
  echo "
  <form method='post' action='about.php'>
      <div class='contact'>Email:</div>
      <input name='email' type='text' id='c' size='55' /><br /><br />
